INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('6618', '{眠;ねむ}りが{浅い;あさい}のです。', 'I sleep superficialy', 'Je dors superficiellement.', '3');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('6619', '{眠い;ねむい}から{帰;かえ}ります。', 'I''m sleepy, so I am leaving now.', 'J''ai sommeil, je vais donc rentrer.', '3');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('6620', '{着物;きもの}{は;わ}{着;き}ますか。', 'Do you wear a kimono?', 'Portes-tu un kimono ?', '3');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('6621', '{石;いし}{は;わ}{浮;う}かない。', 'A stone does not float.', 'Une pierre ne flotte pas.', '3');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('6622', '{社会;しゃかい}{は;わ}ぜんぜん{気;き}にしない。', 'Society could not care less.', 'Cela n''a aucun intérêt pour la société.', '3');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('6623', '{社員;しゃいん}{旅行;りょこう}の{予定;よてい}、あまりぱっとしないよ。', 'The plans for the company outing don''t sound too exciting.', 'Le programme du voyage d''entreprise me semble moyen.', '3');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('6624', '{社長;しゃちょう}{は;わ}{私;わたし}の{歳;とし}の{２;に}{倍;ばい}です。', 'My boss is twice as old as I am.', 'Mon boss a deux fois mon âge.', '3');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('6625', '{社長;しゃちょう}に{電話;でんわ}を{回;まわ}します。', 'I''ll put you through to the president.', 'Je vais vous passer le patron.', '3');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('6626', '{私;わたし}{は;わ}{今;いま}{空港;くうこう}にいます。', 'I''m at the airport now.', 'Je suis actuellement à l''aéroport.', '1');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('6627', '{私;わたし}{は;わ}{今朝;けさ}{犬;いぬ}を{連れて;つれて}{散歩;さんぽ}した。', 'I took a walk with my dog this morning.', 'J''ai emmené mon chien faire une promenade ce matin.', '3');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('6628', '{私;わたし}{は;わ}{君;きみ}ほど{文学;ぶんがく}に{興味;きょうみ}{は;わ}ない。', 'I am not as interested in literature as you.', 'Je ne suis pas aussi intéressé par la littérature que toi.', '3');
