INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('6630', '{私;わたし}{は;わ}{姉;あね}に{相談;そうだん}した。', 'I consulted with my sister.', 'Je me suis entretenu avec ma sœur.', '3');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('6631', '{私;わたし}{は;わ}{娘;むすめ}がいます。', 'I have a daughter.', 'J''ai une fille.', '3');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('6632', '{私;わたし}{は;わ}{学校;がっこう}{時代;じだい}をよく{覚;おぼ}えている。', 'I remember very well of my university period.', 'Je me souviens très bien de ma période universitaire.', '3');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('6633', '{私;わたし}{は;わ}{家;いえ}の{裏;うら}で{泳;およ}ぎ{行;い}く{鱒;ます}を{釣る;つる}のが{好;す}きです。', 'I love to fish in the trout stream behind my house.', 'J''aime pêcher dans la rivière à truites derrière ma maison.', '3');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('6634', '{私;わたし}{は;わ}{店;みせ}が{開く;ひらく}のを{待;ま}っている。', 'I am waiting for the store to open.', 'J''attends que le magasin ouvre.', '3');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('6635', '{私;わたし}{は;わ}{悲しい;かなしい}{映画;えいが}が{嫌;きら}いだ。', 'I don''t like sad movies.', 'Je n''aime pas les films tristes.', '3');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('6636', '{私;わたし}{は;わ}{新;あたら}しい{背広;せびろ}を{買;か}った。', 'I bought a new business suit.', 'J''ai acheté un nouveau costard.', '3');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('6637', '{私;わたし}{は;わ}{明日;あした}{忙;いそが}しい。', 'I''ll be busy tomorrow.', 'Je suis occupé demain.', '3');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('6638', '{私;わたし}{は;わ}{昼食;ちゅうしょく}に{招待;しょうたい}された。', 'I was invited to lunch.', 'J''ai été invité à déjeuner.', '3');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('6639', '{私;わたし}{は;わ}{本;ほん}を{元;もと}の{棚;たな}へ{返;かえ}した。', 'I returned the book to its shelf.', 'J''ai remis le livre sur son étagère.', '3');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('6640', '{私;わたし}{は;わ}{札幌;さっぽろ}の{市民;しみん}です。', 'I am a citizen of Sapporo.', 'Je suis un citoyen de Sapporo.', '3');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('6641', '{私;わたし}{は;わ}{柔らかい;やわらかい}ベッドで{眠;ねむ}るのが{好;す}きです。', 'I like to sleep on a soft bed.', 'J''aime dormir sur un lit mou.', '3');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('6642', '{私;わたし}{は;わ}{森;もり}で{道;みち}に{迷;まよ}った。', 'I lost my way in the woods.', 'J''ai perdu mon chemin dans les bois.', '3');
