INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('6644', '{私;わたし}{は;わ}{歴史;れきし}に{興味;きょうみ}がある。', 'I am interested in history.', 'Je suis intéressé par l''histoire.', '3');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('6645', '{私;わたし}{は;わ}{気分;きぶん}が{悪;わる}い。', 'I''m feeling bad.', 'Je ne me sens pas bien.', '3');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('6646', '{私;わたし}{は;わ}{漫画;まんが}を{読;よ}みます。', 'I read comic books.', 'Je lis des mangas.', '3');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('6647', '{私;わたし}{は;わ}{草;くさ}の{上;うえ}に{寝;ね}た。', 'I slept on the grass.', 'J''ai dormi dans l''herbe.', '3');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('6648', '{私;わたし}{は;わ}{訳;わけ}もなく{悲;かな}しくなった。', 'I felt sad for no reason.', 'Je me sentais triste pour aucune raison.', '3');
