INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('6650', '{私;わたし}{は;わ}{２２;にじゅうに}{歳;さい}の{男性;だんせい}です。', 'I am a 22 year-old man.', 'Je suis un homme de 22 ans.', '3');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('6651', '{私;わたし}{は;わ}、{急ぐ;いそぐ}{必要;ひつよう}{は;わ}なかったのに。', 'I needn''t have hurried.', 'Je n''avais pas besoin de me dépêcher.', '3');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('6652', '{私;わたし}{は;わ}あなたと{踊り;おどり}たい。', 'I''d like to dance with you.', 'J''aimerais danser avec toi.', '3');
