INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('6656', '{私;わたし}{達;たち}{は;わ}{月;つき}に{１回;いっかい}ここに{集まる;あつまる}。', 'We gather here once a month.', 'Nous nous réunissons ici une fois par mois.', '3');
