INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('6659', '{私;わたし}たち{は;わ}{池;いけ}の{周り;まわり}を{歩;ある}いた。', 'We walked around the pond.', 'Nous avons marché autour du lac.', '3');
