INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('6661', '{私;わたし}たち{は;わ}{険;けわ}しい{坂;さか}を{登;のぼ}った。', 'We climbed the steep slope.', 'Nous avons grimpé la pente raide.', '3');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('6662', '{私;わたし}たち{は;わ}{高校生;こうこうせい}です。', 'We are high school students.', 'Nous sommes lycéens.', '3');
