INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('6664', '{私;わたし}の{兄;あに}{は;わ}{大学生;だいがくせい}です。', 'My brother is a college student.', 'Mon frère est étudiant.', '3');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('6665', '{私;わたし}の{夫;おっと}{は;わ}とても{料理;りょうり}が{上手;じょうず}です。', 'My husband is a very good cook.', 'Mon mari est un doué en cuisine.', '3');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('6666', '{私;わたし}の{妻;つま}{は;わ}{中国人;ちゅうごくじん}です。', 'My wife is Chinese.', 'Ma femme est chinoise.', '3');
