INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('6835', '{先生;せんせい}が{学生;がくせい}を{呼ぶ;よぶ}。', 'The teacher calls the students.', 'Le professeur appelle les élèves.', '2');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('6836', '{先生;せんせい}が{来;く}る。 ', 'There comes the teacher. ', 'Le professeur arrive.', '2');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('6837', '{先週;せんしゅう}{一;いち}{日;にち}{休;やす}みを{取;と}った。 ', 'I took a day off last week. ', 'J''ai pris un jour de repos la semaine dernière.', '2');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('6838', '{入口;いりぐち}{は;わ}どちらですか。', 'Where is the way in?', 'Où est l''entrée ?', '2');
