INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('6840', '{公園;こうえん}で{お弁当;おべんとう}を{食;た}べませんか。 ', 'How about having a lunch box in the park? ', 'Mangeons une lunch box dans le parc.', '2');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('6841', '{公園;こうえん}を{散歩;さんぽ}しましょう。 ', 'Let''s take a walk in the park. ', 'Allons nous promener dans le parc.', '2');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('6842', '{六;ろく}{月;がつ}に{は;わ}{雨;あめ}が{多;おお}い。 ', 'We have a lot of rain in June. ', 'Nous avons beaucoup de pluie en juin.', '2');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('6843', '{再来年;さらいねん}{は;わ}１８{歳;さい}になる。 ', 'I''ll be eighteen in two years. ', 'J''aurai 18 ans dans deux ans.', '2');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('6844', '{写真;しゃしん}を{撮る;とる}。 ', 'To take a picture. ', 'Prendre une photo.', '2');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('6845', '{冬;ふゆ}に{は;わ}{雪;ゆき}が{多い;おおい}。 ', 'We have a lot of snow in winter. ', 'Nous avons beaucoup de neige en hiver.', '2');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('6846', '{冷たい;つめたい}{水;みず}をもってきて{下;くだ}さい。', 'Bring me some cold water. ', 'Apportez-moi de l''eau fraîche.', '2');
