INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('6850', '{切符;きっぷ}を{買;か}いました。 ', 'I bought a ticket. ', 'J''ai acheté un ticket.', '2');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('6851', '{前;まえ}{払;はら}いですか。 ', 'Do I have to pay before? ', 'Dois-je payer d''abord ?', '2');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('6852', '{助;たす}けてと{頼む;たのむ}。 ', 'To ask for help. ', 'Demander de l''aide.', '2');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('6853', '{午後;ごご}{は;わ}{休;やす}みだった。 ', 'We had the afternoon off. ', 'Nous avons eu l''après-midi de repos.', '2');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('6854', '{危ない;あぶない}！トラックが{来;く}るぞ！ ', 'Look out! There''s a truck coming! ', 'Attention ! Un camion arrive.', '2');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('6855', '{友達;ともだち}と{ご飯;ごはん}を{食;た}べに{行;い}く。 ', 'I am going to eat a meal with a friend. ', 'Je vais manger un repas avec un ami.', '2');
