INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('7053', '{私;わたし}たち{は;わ}{耳;みみ}で{聞く;きく}。 ', 'We hear with ears. ', 'Nous entendons avec nos oreilles.', '2');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('7054', '{私;わたし}の{お母さん;おかあさん}{は;わ}{美;うつく}しいです。 ', 'My mother is beautiful. ', 'Ma mère est magnifique.', '2');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('7055', '{私;わたし}の{兄;あに}{は;わ}ギターを{弾く;ひく}。 ', 'My brother plays the guitar. ', 'Mon frère joue de la guitare.', '2');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('7056', '{私;わたし}の{兄;あに}がこの{自転車;じてんしゃ}を{使う;つかう}。 ', 'My brother uses this bike. ', 'Mon frère utilise ce vélo.', '2');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('7057', '{私;わたし}の{妹;いもうと}{は;わ}{有名;ゆうめい}だ。 ', 'My sister is famous. ', 'Ma petite soeur est célèbre.', '2');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('7058', '{私;わたし}の{姉;あね}{は;わ}{２０;にじゅう}{代;だい}だ。 ', 'My sister is in her twenties. ', 'Ma soeur a la vingtaine.', '2');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('7059', '{私;わたし}の{家;いえ}{は;わ}{駅;えき}から{近い;ちかい}。 ', 'My house is near the station. ', 'Ma maison est proche de la gare.', '2');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('7060', '{私;わたし}の{家;いえ}の{後ろ;うしろ}{は;わ}{森;もり}です。 ', 'My house is backed by woods. ', 'Il y a une forêt derrière ma maison.', '2');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('7061', '{私;わたし}の{時計;とけい}{は;わ}{二;に}{分;ふん}{早;はや}い。 ', 'My clock is two minutes fast. ', 'Ma montre est en avance de deux minutes.', '2');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('7062', '{私;わたし}の{机;つくえ}{は;わ}{古い;ふるい}。 ', 'My desk is old. ', 'Mon bureau est vieux.', '2');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('7063', '{私;わたし}の{父;ちち}{は;わ}{忙しい;いそがしい}。 ', 'My father is busy. ', 'Mon père est occupé.', '2');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('7064', '{私;わたし}の{犬;いぬ}{は;わ}{白;しろ}い。 ', 'My dog is white. ', 'Mon chien est blanc.', '2');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('7065', '{私;わたし}の{犬;いぬ}{は;わ}{白い;しろい}。 ', 'My dog is white. ', 'Mon chien est blanc.', '2');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('7066', '{私;わたし}の{趣味;しゅみ}{は;わ}{音楽;おんがく}だ。 ', 'My hobby is music. ', 'Mon hobby est la musique.', '2');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('7067', '{私;わたし}のコートは{軽い;かるい}が{暖;あたた}かい。 ', 'My overcoat is light but warm. ', 'Mon manteau est léger mais chaud.', '2');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('7068', '{私;わたし}も{十;じゅっ}{歳;さい}です。 ', 'I am ten years old, too. ', 'J''ai dix ans aussi.', '2');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('7069', '{私;わたし}も。 ', 'So do I. ', 'Moi aussi.', '2');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('7070', '{秋;あき}{は;わ}{月;つき}が{美;うつく}しい。 ', 'The moon is beautiful in fall. ', 'La lune est magnifique en automne.', '2');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('7071', '{空;そら}{は;わ}{青;あお}い。 ', 'The sky is blue. ', 'Le ciel est bleu.', '2');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('7072', '{紅茶;こうちゃ}をいただけますか。 ', 'Could you get me some black tea? ', 'Pourrais-je avoir du thé noir ?', '2');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('7073', '{紙;かみ}がほしい。 ', 'I need some paper. ', 'J''ai besoin de papier.', '2');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('7074', '{結婚;けっこん}して{五;ご}{年;ねん}になります。 ', 'We''ve been married for five years. ', 'Nous sommes mariés depuis cinq ans.', '2');
