INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('7076', '{肉;にく}{は;わ}{欲;ほ}しくない。 ', 'I don''t want meat. ', 'Je ne veux pas de viande.', '2');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('7077', '{船;ふね}{は;わ}{橋;はし}の{下;した}を{通;とお}った。 ', 'The boat passed under the bridge. ', 'Le bateau est passé sous le pont.', '2');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('7078', '{英語;えいご}{は;わ}{難しい;むずかしい}ですね。 ', 'English is difficult, isn''t it? ', 'L''anglais est difficile, n''est pas ?', '2');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('7079', '{英語;えいご}を{話す;はなす}. ', 'To speak English. ', 'Parler en anglais.', '2');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('7080', '{茶;ちゃ}が{要る;いる}。 ', 'To need tea.', 'Avoir besoin de thé.', '2');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('7081', '{荷物;にもつ}{は;わ}{３;みっ}つあります。 ', 'I have three pieces of baggage. ', 'J''ai trois bagages.', '2');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('7082', '{誕生日;たんじょうび}{は;わ}いつですか。 ', 'When is your birthday? ', 'Quand est ton anniversaire ?', '2');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('7083', '{財布;さいふ}にお{金;かね}をいれた。 ', 'I put my money in a wallet. ', 'J''ai mis mon argent dans un portefeuille.', '2');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('7084', '{財布;さいふ}をヒータの{上;うえ}に{置く;おく}な。 ', 'Don''t put the wallet on the top of the heater. ', 'Ne mets pas ton portefeuille au-dessus du radiateur.', '2');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('7085', '{買い物;かいもの}{一緒;いっしょ}に{行;い}く？ ', 'Would you like to go shopping with me? ', 'On va faire du shopping ensemble ?', '2');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('7086', '{質問;しつもん}に{答;こた}えなさい。 ', 'Answer the question. ', 'Réponds à la question.', '2');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('7087', '{起きる;おきる}{時間;じかん}よ。 ', 'Time to get up. ', 'C''est l''heure de se lever.', '2');
