INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('7092', '{辞書;じしょ}が{机;つくえ}の{上;うえ}にある。 ', 'There is a dictionary on the desk. ', 'Il y a un dictionnaire sur la table.', '2');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('7093', '{近;ちか}くに{病院;びょういん}がある。 ', 'There is a hospital near by. ', 'Il y a un hôpital dans le voisinage.', '2');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('7094', '{近く;ちかく}に{病院;びょういん}がある。 ', 'There is a hospital near by. ', 'Il y a un hôpital dans le voisinage.', '2');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('7095', '{道;みち}を{教;おし}えます。 ', 'I''ll show you the way. ', 'Je vais te montrer le chemin.', '2');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('7096', '{部屋;へや}に{は;わ}{誰か;だれか}いますか。 ', 'Is there anyone in the room? ', 'Il y a quelqu''un dans la chambre ?', '2');
