INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('7098', '{郵便;ゆうびん}{は;わ}{昼;ひる}{前;まえ}に{来;く}る。 ', 'The mail arrives before noon. ', 'Les lettres arrivent avant midi.', '2');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('7099', '{金;かね}がないと{友達;ともだち}も{少ない;すくない}。 ', 'Little money, few friends. ', 'Peu d''argent, peu d''amis.', '2');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('7100', '{銀行;ぎんこう}{は;わ}{９;く}{時;じ}に{開く;ひらく}。', 'Banks open at nine o''clock. ', 'Les banques ouvrent à 9h.', '2');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('7101', '{門;もん}を{閉;し}める{時間;じかん}だ。 ', 'It is time to shut the gate. ', 'C''est l''heure de fermer le portail.', '2');
