INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('7109', '{靴下;くつした}を{脱;ぬ}いでください。 ', 'Take off your socks, please. ', 'Enlève tes chaussettes s''il te plait.', '2');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('7110', '{頭;あたま}が{痛い;いたい}。 ', 'I have a headache. ', 'J''ai mal à la tête.', '2');
