INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('7112', '{顔;かお}を{洗;あら}いなさい。 ', 'Wash your face.', 'Lave ton visage.', '2');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('7113', '{風;かぜ}{は;わ}{北;きた}から{吹;ふ}いている。 ', 'The wind is blowing from the north. ', 'Le vent souffle du nord.', '2');
