INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('7115', '{風;かぜ}{は;わ}{西;にし}から{吹;ふ}いている。 ', 'The wind is blowing from the west. ', 'Le vent souffle de l''ouest.', '2');
