INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('7119', '{食堂;しょくどう}{は;わ}どこですか。 ', 'Where is the cafeteria? ', 'Où est la cafétéria ?', '2');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('7120', '{駅;えき}の{前;まえ}に{郵便局;ゆうびんきょく}があります。 ', 'There is a post office in front of the station. ', 'Il y a un bureau de poste en face de la gare.', '2');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('7121', '{駅前;えきまえ}に{銀行;ぎんこう}がある。 ', 'There is a bank in front of the station. ', 'Il y a une banque en face de la gare.', '2');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('7122', '{鳥;とり}{は;わ}{歌;うた}います。 ', 'Birds sing. ', 'Les oiseaux chantent.', '2');
