INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('7124', '{３;さん}{人;にん}の{留学生;りゅうがくせい}がいます。', 'There are three exchange students.', 'Il y a 3 étudiants étrangers.', '2');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('7125', '{５０;ごじゅう}{円;えん}です。 ', 'It''s 50 yen. ', 'C''est 50 yens.', '2');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('7126', '{７;なな}{円;えん}の{切手;きって}ありませんか。 ', 'Have you got a seven-yen stamp? ', 'N''auriez-vous pas un timbre à 7 yens ?', '2');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('7127', 'あなたの{住;す}んでいる{所;ところ}{は;わ}{遠;とお}いですか？', 'Is the place you live far?', 'L''endroit où tu vis est-il loin ?', '2');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('7128', 'あの{人;ひと}{誰;だれ}？ ', 'Who is that person? ', 'Qui est cette personne ?', '2');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('7129', 'あの{女;おんな}の{人;ひと}{は;わ}{誰;だれ}ですか。 ', 'Who is that woman? ', 'Qui est cette femme ?', '2');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('7130', 'あの{女の人;おんなのひと}{は;わ}{誰;だれ}ですか。 ', 'Who is that woman? ', 'Qui est cette femme ?', '2');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('7131', 'あの{建物;たてもの}を{見;み}て。 ', 'Look at that building. ', 'Regarde ce bâtiment.', '2');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('7132', 'あの{男の人;おとこのひと}{は;わ}{誰;だれ}ですか。 ', 'Who is that man? ', 'Qui est cet homme ?', '2');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('7133', 'あの{部屋;へや}の{中;なか}{は;わ}{暗い;くらい}。 ', 'It is dark in that room. ', 'Il fait sombre dans cette chambre.', '2');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('7134', 'いい{天気;てんき}ですね。 ', 'Good weather, isn''t it?', 'Beau temps n''est-ce pas ?', '2');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('7135', 'いい{奥さん;おくさん}になるよ。 ', 'You will make a good wife. ', 'Tu feras une bonne femme.', '2');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('7136', 'いつ{終わる;おわる}の？ ', 'When does it end? ', 'Quand cela finit-il ?', '2');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('7137', 'いろいろな{お菓子;おかし}があった。 ', 'There were various kinds of sweets. ', 'Il y avait des gâteaux variés.', '2');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('7138', 'お{仕事;しごと}{は;わ}{何;なん}ですか。 ', 'What is your job? ', 'Quel est votre travail ?', '2');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('7139', 'お{元気;げんき}ですか。 ', 'How are you doing? ', 'Comment vas-tu ?', '2');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('7140', 'お{金;かね}{は;わ}{少し;すこし}ある。 ', 'I have a little money. ', 'J''ai un peu d''argent.', '2');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('7141', 'お{金;かね}{は;わ}すべてのものに{答える;こたえる}。 ', 'Money is the answer to everything.', 'L''argent est la réponse à tout.', '2');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('7142', 'お{金;かね}が{欲しい;ほしい}。 ', 'I want money. ', 'Je veux de l''argent.', '2');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('7143', 'お{金;かね}を{貸す;かす}{事;ごと}{は;わ}{出来;でき}ません。 ', 'I can''t lend you any money. ', 'Je ne peux pas te prêter d''argent.', '2');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('7144', 'お{飲み物;のみもの}{は;わ}{何;なん}ですか。 ', 'What would you like to drink? ', 'Quelle boisson veux-tu ?', '2');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('7145', 'ここに{座る;すわる}。 ', 'To sit here. ', 'S''asseoir ici.', '2');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('7146', 'この{写真;しゃしん}{大好き;だいすき}。 ', 'I love this photo. ', 'J''aime beaucoup cette photo.', '2');
