INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('7148', 'この{国;くに}に{来;き}た{目的;もくてき}{は;わ}？ ', 'What''s your purpose in visiting this country? ', 'Quel est ton but en visitant ce pays ?', '2');
INSERT INTO "main"."sentences" ("_id", "jap", "en", "fr", "level") VALUES ('7149', 'この{山;やま}を{登る;のぼる}の{は;わ}{危険;きけん}だ。 ', 'It''s dangerous to climb this mountain. ', 'Il est dangereux d''escalader la montagne.', '2');
